// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IHederaTokenService {
    // Token info struct
    struct HederaToken {
        string name;
        string symbol;
        address treasury;
        string memo;
        bool supplyType;
        uint32 maxSupply;
        bool freezeDefault;
        address[] freezeKey;
        address[] wipeKey;
        address[] supplyKey;
        address[] adminKey;
        address[] kycKey;
        uint8 decimals;
        address autoRenewAccount;
        uint32 autoRenewPeriod;
    }

    function associateToken(address account, address token) external returns (int64);
    function disassociateToken(address account, address token) external returns (int64);
    function transferToken(address token, address from, address to, uint256 amount) external returns (int64);
    function mintToken(address token, uint256 amount, bytes[] memory metadata) external returns (int64);
    function burnToken(address token, uint256 amount, bytes[] memory metadata) external returns (int64);
    function allowance(address token, address owner, address spender) external view returns (uint256);
    function balanceOf(address token, address account) external view returns (uint256);

    // Token creation function
    function createToken(
        HederaToken memory token,
        uint initialTotalSupply,
        uint8[] memory keys,
        address[] memory keyAddresses
    ) external payable returns (int64 responseCode, address tokenAddress);

    // Key query functions
    function isSupplyKey(address token, address supplyAddress) external view returns (bool);

    // Token association check
    function isTokenAssociated(address account, address token) external view returns (bool);

    function setAllowance(address token, address owner, address spender, uint256 amount) external;
    function setBalance(address token, address account, uint256 amount) external;
    function setTransferResult(address token, int64 result) external;
    function clearTransferResults() external;

    // Mock functions for testing
    function setMockedCreateTokenResponse(int64 responseCode, address tokenAddress) external;
    function setupTokens(address tokenAddress, address sauceTokenAddress, address clxyTokenAddress, address userAddress) external;
}

